import React, { useEffect, useState } from 'react';
import './css/Job_listing.css';
import axios from 'axios';
import Job_details from './Job_details'
import {Link} from 'react-router-dom'
const UserContext = React.createContext();
class Joblist extends React.Component{
  constructor(){
    this.state={
      jobs:[],
      jobLocation:"",  //For filter by location 
      displayContainer:"block",     // For displaying the div
      displayDiv:"",// For filter by name
      jobType:"",//For filter by job type
      jobExperience:"",//For Filter by experience
      isToggle:false  //ForRead More
    }
    // this.bringJobs=this.bringJobs.bind(this);
    // this.setJobLocation=this.setJobLocation.bind(this);
    // this.setDisplayContainer=this.setDisplayContainer.bind(this);
    // this.setDisplay=this.setDisplay.bind(this);
    // this.setJobType=this.setJobType.bind(this);
    // this.setJobExperience=this.setJobExperience.bind(this);
   this.handleClick=this.handleClick.bind(this);
    this.filterJobDepartment=this.filterJobDepartment.bind(this);
    this.filterJobType=this.filterJobType.bind(this);
    this.filterJobLocation=this.filterJobLocation.bind(this);
    this.filterJobExperience=this.filterJobExperience.bind(this);
    this.handelDetalis=this.handelDetalis.bind(this);
    this.ReadMore=this.ReadMore.bind(this);

  } 
  
    //  const [jobs,bringJobs]=useState([]);
   
    //  const [jobLocation,setJobLocation]=useState("");

    //  const [displayContainer,setDisplayContainer]=useState("block");
     
    //  const [displayDiv,setDisplay]=useState("");
     
    //  const [jobType,setJobType]=useState("");
     
    //  const [jobExperience,setJobExperience]=useState("");
   
    //  const [isToggle,handleClick]=useState(false);
        //  Filter by Department's name
          filterJobDepartment=(e)=>{
      const checked=e.target.value;
      if(checked){
        this.state.setState(
          {displayContainer:"none",
        setDisplay:checked
        })
        
      
        
      }else if(!checked){
        this.state.setState(
          {displayContainer:"block"
        })
      }
    }    
    //Filter by job type
     filterJobType=(e)=>{
      const selectJobType=e.target.value;
      if(selectJobType){
        this.state.setState(
          {displayContainer:"none",
          setJobType:selectJobType
        })
      }else if(!selectJobType){
        this.state.setState(
          {displayContainer:"block"
        })
       
      }
    } 
    // Filter by job location
      filterJobLocation=(e)=>{
      const selected=e.target.value;
      if(selected){
        this.state.setState(
          {displayContainer:"none",
          setJobLocation:selected
        })
        
      }else if(!selected){
        this.state.setState(
          {displayContainer:"block"
        })
       
      }
    }   
    //Filter by experience
     filterJobExperience=(e)=>{
      const selectedExperience=e.target.value;
      if(selectedExperience){
        this.state.setState(
          {displayContainer:"none",
          setJobExperience:selectedExperience
        })
        
      }else if(!selectedExperience){
        this.state.setState(
          {displayContainer:"block"
        })
       
      }
    }
    handelDetalis=()=>{
      window.location='./Job_details'
    }
    ReadMore=(event)=>{
      this.state.setState({})
      handleClick(!isToggle)
     
     
  }
   render(){
     
      
    //  })




  axios.get(`http://localhost/jobhooks/API/bringJobs.php`)
  .then((res)=>{
  // console.log(res)
  const jobsData=res.data;
  bringJobs(jobsData);
  // console.log(jobsSecond)
  
  });
    return(
  

      
<>

{/* Preloader Start  */}
    {/* <div id="preloader-active">
        <div class="preloader d-flex align-items-center justify-content-center">
            <div class="preloader-inner position-relative">
                <div class="preloader-circle"></div>
                <div class="preloader-img pere-text">
                    <img src="assets/img/logo/logo.png" alt="" />
                </div>
            </div>
        </div>
    </div> */}
    {/* Preloader Start  */}

  <main>
    {/* Hero Area Start*/}
    <div className="slider-area ">
      <div
        className="single-slider section-overly slider-height2 d-flex align-items-center"
        data-background="assets/img/hero/about.jpg"
      >
        <div className="container">
          <div className="row">
            <div className="col-xl-12">
              <div className="hero-cap text-center">
                <p className='hero-text'>Get your job</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
    {/* Hero Area End */}
    {/* Job List Area Start */}
    <div className="job-listing-area pt-120 pb-120">
      <div className="container">
        <div className="row job-category-listing mb-50">
          {/* Left content */}
          <div className="col-xl-3 col-lg col-md-4">
            
              <div className="col-lg-12">
                <div className="small-section-tittle2 mb-45">
                  <div className="ion">
                    {" "}
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      xmlnsXlink="http://www.w3.org/1999/xlink"
                      width="20px"
                      height="12px"
                    >
                      <path
                        fillRule="evenodd"
                        fill="rgb(27, 207, 107)"
                        d="M7.778,12.000 L12.222,12.000 L12.222,10.000 L7.778,10.000 L7.778,12.000 ZM-0.000,-0.000 L-0.000,2.000 L20.000,2.000 L20.000,-0.000 L-0.000,-0.000 ZM3.333,7.000 L16.667,7.000 L16.667,5.000 L3.333,5.000 L3.333,7.000 Z"
                      />
                    </svg>
                  </div>
                  <h4>Filter Jobs</h4>
                  <span>please use filters to reach your job wisely and specifically</span>
